import React from 'react';

import './signIn.styles.scss';

class SignIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        this.setState({ email: '', password: '' })
    }

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value });
    }

    render() {
        return(
            <div className='signin'>
                <h3 className='signin__heading'>I already have an account</h3>
                <span className='signin__instruction'>Sign in using the given credentials</span>

                <form className='signin__form' onSubmit={this.handleSubmit}>
                    <input name='email' type='email' value={this.state.email} onChange={this.handleChange} required></input>
                    <label>Email</label>
                    <input name='password' type='password' value={this.state.password} onChange={this.handleChange} required></input>
                    <label>Password</label>

                    <input type='submit' value='Submit Form'></input>
                </form>
            </div>
        )
    }
}

export default SignIn;
