import React from 'react';
import './App.css';
import HomePage from '../src/pages/home-page/homepage.component';
import ShopPage from '../src/pages/shop/shop.component';
import Header from '../src/components/header/header.components';
import SignIn from '../src/components/signIn/signIn.component';
import SignUp from '../src/components/sign-up/signUp.component';

import { auth, createUserProfileDocument } from './firebase/firebase.utils';

import { BrowserRouter, Switch, Route } from 'react-router-dom';



class App extends React.Component {
	constructor() {
		super();

		this.state = {
			currentUser: null
		};
	}

	unsubscribeFromAuth = null;

	componentDidMount() {
		this.unsubscribeFromAuth =auth.onAuthStateChanged(async userAuth => {
			if(userAuth) {
				const userRef = await createUserProfileDocument(userAuth);

				userRef.onSnapshot(snapshot => {
					this.setState({
						currentUser: {
							id: snapshot.id,
							...snapshot.data()
						}
					}, () => {
						console.log(this.state);
					}
					)
				});
			}

			else {
				this.setState({currentUser: userAuth});
			}
		})
	}

	componentWillUnmount() {
		this.unsubscribeFromAuth();
	}



	render() {
		return (
			<div className="App">
				<BrowserRouter>
					<Header currentUser={this.state.currentUser} />
					<Switch>
						<Route exact path='/' component={HomePage} />
						<Route exact path='/shop' component={ShopPage} />
						<Route exact path='/signin' component={SignIn} />
						<Route exact path='/signup' component={SignUp} />
					</Switch>
				</BrowserRouter>

			</div>
		  );
	}

}

export default App;
