import React from 'react';

import './signIn.styles.scss';

import FormInput from '../form-input/formInput.component';

class SignIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        this.setState({ email: '', password: '' })
    }

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value });
    }

    render() {
        return(
            <div className='signin'>
                <h3 className='signin__heading'>I already have an account</h3>
                <span className='signin__instruction'>Sign in using the given credentials</span>

                <form className='signin__form' onSubmit={this.handleSubmit}>
                    <FormInput name='email' type='email' value={this.state.email} handleChange={this.handleChange} label={label} required />
                    
                    <FormInput name='password' type='password' value={this.state.password} handleChange={this.handleChange} label={label} required />
                    

                    <input type='submit' value='Submit Form'></input>
                </form>
            </div>
        )
    }
}

export default SignIn;
